import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import DataTable from './components/DataTable';
import Footer from './components/Footer';

// Main App component: Fetches data from API and manages state
function App() {
  const [data, setData] = useState([]); // State to hold fetched data
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state
  const [searchTerm, setSearchTerm] = useState(''); // Search term for filtering

  // API endpoint with provided key and parameters (fetch all available data; adjust limit if needed)
  const API_URL = 'https://api.data.gov.in/resource/19697d76-442e-4d76-aeae-13f8a17c91e1?api-key=579b464db66ec23bdd00000154c4fad961a54f2156e60124748b8649&format=json&limit=5000';

  // Fetch data on component mount
  useEffect(() => {
    fetch(API_URL)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(json => {
        // Assuming API response has 'records' array (standard for data.gov.in)
        setData(json.records || []);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, []); // Empty dependency array: run once on mount

  // Filter data based on search term (searches in station_name and state_name)
  const filteredData = data.filter(item =>
    (item.station_name && item.station_name.toLowerCase().includes(searchTerm.toLowerCase())) ||
    (item.state_name && item.state_name.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  return (
    <div className="App">
      <Header />
      <main>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search by station or state..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
        {loading && <p>Loading data...</p>}
        {error && <p>Error: {error}</p>}
        {!loading && !error && <DataTable data={filteredData} />}
      </main>
      <Footer />
    </div>
  );
}

export default App;
